mapper.update:
update bl_tag
set tag_name    = #{tagName,jdbcType=VARCHAR},
    create_time = #{createTime,jdbcType=TIMESTAMP},
    update_time = #{updateTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, tag_name, create_time, update_time
from bl_tag
where id = #{id,jdbcType=INTEGER}

select id, tag_name, create_time, update_time
from bl_tag
where id = #{tagId}

select id, tag_name, create_time, update_time
from bl_tag
where id IN
(

    #{tagId}

)
